mapper.insert:
	  INSERT INTO WORK ( work_no
						,work_equip_no
						,work_name
						,work_content
						,work_company
                        ,work_pay
						,work_date
						,work_end_date
						,work_ps
						,work_code
						)
		         values ( #{workNo}
				        ,(SELECT equip_no
					      FROM equip
					      WHERE equip_name = #{workEquipName})
					    ,#{workName}
				        ,#{workContent}
				        ,#{workCompany}
				        ,#{workPay}
				        ,#{workDate}
				        ,#{workEndDate}
				        ,#{workPs}
				        ,'C0101')

mapper.update:
	 update work
	 set work_code = 'B0101'
     where work_no = #{workNo}

UPDATE work
SET work_no = work_no-1
WHERE work_no > #{workNo}

UPDATE work_sign
SET sign_work_no = sign_work_no-1
WHERE sign_work_no > #{workNo}

UPDATE part

set	part_equip_no = (select equip_no from equip where equip_name = #{partEquipName})

WHERE part_no = #{partNo}
