mapper.select:
SELECT mio_no, m.m_name as mio_name, mio.mio_mo_order_id as mo_order_id, mio.mio_quantity AS mio_qntt, mio.mio_date FROM material_io mio JOIN material m ON m.m_no = mio.mio_no WHERE mio.mio_progress = 'L0103'
AND TRUNC(mio.mio_date) BETWEEN #{startDate} AND #{endDate}
ORDER BY mio_date

SELECT mio_no, m.m_name as mio_name, mio.mio_quantity AS mio_qntt, mio.mio_date FROM material_io mio JOIN material m ON m.m_no = mio.mio_no WHERE mio_progress = 'L0104'
AND TRUNC(mio.mio_date) BETWEEN #{startDate} AND #{endDate}
ORDER BY mio_date

mapper.update:
UPDATE material_order 
SET mo_progress_code = 'L0103'
WHERE mo_order_id =#{moOrderId}

UPDATE material
SET m_stock = m_stock + #{mioQntt}
WHERE m_no = #{mNo}
